mapper.insert:
INSERT INTO academy
   SET user_id = #{userId}
     , dong_id = #{dongId}
     , aca_name = #{acaName}
     , aca_phone = #{acaPhone}
     , comment = #{comment}
     , teacher_num = #{teacherNum}
     , open_time = #{openTime}
     , close_time = #{closeTime}
     , address = #{address}
     , aca_pic = #{acaPic}

mapper.update:
UPDATE academy


        aca_name = #{acaName}


        , acaPhone = #{acaPhone}


        , comment = #{comment}


        , teacherNum = #{teacherNum}


        , openTime = #{openTime}


        , closeTime = #{closeTime}


        , acaPic = #{acaPic}


WHERE acaId = #{acaId}

mapper.delete:
DELETE FROM academy
 WHERE aca_id = #{acaId}
   AND user_id = #{userId}
